char*c="#version 330\nin vec3 P;void main(){gl_Position=vec4(P,1);}";char*f="#version 330\nuniform vec3 color;out vec4 o;void main(){o=vec4(color,1);}";float t=1/3.,h=.5;vec3 v(float x,float y=t){return vec3(x,y,1);}class A:glApp{Geometry<vec3>*p;GPUProgram*g;public:A():glApp(""){}void onDisplay(){p=new Geometry<vec3>;g=new GPUProgram(c,f);glPointSize(9);glLineWidth(3);p->Vtx()={v(-1,.65),v(1,.05),v(-1),v(1)};p->updateGPU();p->Draw(g,1,v(0,1));p->Vtx()={v(-h,h),v(-t/2,.4),v(t/5),v(-h,-h),v(h,-h)};p->updateGPU();p->Draw(g,0,vec3(1,0,0));}};A a;